
public class ArrithOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int x=22, y=44;
    double i= 11.4;
	double j=22.4;
    System.out.println("x+y =" +(x+y));
    System.out.println("x*i =" +(x*i));
    System.out.println("j/i =" +(j/i));
    System.out.println("i-x =" +(i-x));
    
	}

}
