
public class OddNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int num =99;
      System.out.println("List of odd number from 0 to "+ num +" :");
      for(int i=0;i<=num;i++) {
    	  if(i%2!=0) {
    		  System.out.println(i + "");
    	  }
      }
	}

}
